		AND o.order_no = #{order_no}


ORDER BY o.tracked_date DESC

SELECT o.commission_id ,o.order_id ,o.member_id ,o.stores_id ,o.member_phone ,o.amount ,o.commision_status ,o.created_time ,o.modified_date ,o.remark ,(oi.sale_fee * oi.qty) AS sale_fee FROM fg_order_commission o left join fg_order oi on oi.order_no = o.order_id
1=1
AND o.commission_id = #{commission_id}
AND o.order_id = #{order_id}
AND o.stores_id = #{stores_id}
AND o.member_phone = #{member_phone}
AND o.amount = #{amount}
AND o.commision_status = #{commision_status}
ORDER BY o.created_time DESC

SELECT o.order_id ,o.order_no ,o.order_date ,o.order_type ,o.payment_way_key ,o.desc1 ,o.total_fee ,o.discount_fee ,o.sale_fee ,o.delivery_address ,o.contact_person ,o.contact_tel ,o.logistics ,o.data_source ,o.external_order_no ,o.remark ,o.biz_remark ,o.status ,o.settle_status ,o.qty ,oi.sku_id ,oi.goods_id as item_goods_id ,oi.goods_code as item_goods_code ,oi.goods_title as item_goods_title ,oi.qty as item_qty ,oi.sale_price as item_sale_price ,oi.goods_pic_info as item_goods_pic_info ,oi.remark as item_remark ,oi.taobao_link as item_taobao_link FROM fg_order o inner join fg_order_item oi on oi.order_id = o.order_id
1=1 AND o.order_type ='meitianhui' AND o.status <> 'refund'
AND o.member_id = #{member_id}
AND o.member_type_key = #{member_type_key}
AND o.status <> #{status_neq}
AND o.status = #{status}
AND o.order_date >= #{order_date_start}
AND o.order_date <= date_add(#{order_date_end},interval 1 day)
AND o.member_mobile = #{member_mobile}
AND o.member_info like CONCAT('%',#{member_info_like},'%')
AND oi.goods_code = #{goods_code}
ORDER BY o.order_date DESC

SELECT o.order_id ,o.order_no ,o.transaction_no ,o.order_date ,o.amount ,o.reward_point ,o.reward_shell ,o.payment_way_key ,o.member_type_key ,o.member_id ,o.member_info ,o.status ,o.created_date ,o.modified_date ,o.remark ,o.trade_type_key ,o.data_source FROM rc_order o
1=1
AND o.transaction_no = #{transaction_no}
AND o.payment_way_key = #{payment_way_key}
AND o.member_id = #{member_id}
AND o.order_type_key = #{order_type_key}

SELECT o.order_id ,o.order_no ,o.transaction_no ,o.order_date ,o.amount ,o.reward_point ,o.reward_shell ,o.payment_way_key ,o.member_type_key ,o.member_id ,o.member_info ,o.status ,o.created_date ,o.modified_date ,o.remark ,o.trade_type_key ,o.data_source FROM rc_order o
1=1
AND o.transaction_no = #{transaction_no}
AND o.payment_way_key = #{payment_way_key}
AND o.member_id = #{member_id}
AND o.order_type_key = #{order_type_key}
AND o.status = #{status}
AND created_date >= #{created_date_start}
AND created_date < date_add(#{created_date_end},interval 1 day)
ORDER BY created_date DESC

SELECT o.log_id ,o.trade_no ,o.order_no ,o.amount ,o.payment_way_key ,o.status ,o.created_date ,o.modified_date ,o.remark ,o.trade_type_key ,o.data_source FROM rc_order_trade_log o
1=1
AND o.trade_no = #{trade_no}

